    public static class WeaponEntityExtensionMethods
    {
        public static void SetVisible (this FlatRedBall.Math.PositionedObjectList<WeaponEntity> list, bool value) 
        {
            int count = list.Count;
            for (int i = 0; i < count; i++)
            {
                list[i].Visible = value;
            }
        }
